export default {
  props: {
    /**
     * 图标类型
     */
    type: String,

    /**
     * 图标颜色
     */
    color: String,

    /**
     * 图标大小
     */
    size: {
      type: [Number, String],
      default: 24
    }
  },
  computed: {
    fontSize: function fontSize() {
      var size = Number(this.size);
      size = isNaN(size) ? 24 : size;
      return "".concat(size, "px");
    }
  },
  methods: {
    onClick: function onClick() {
      this.$emit('click');
    }
  }
};
