import service from '../../service.js';
import mInput from '../../components/m-input.vue';
export default {
  components: {
    mInput: mInput
  },
  data: function data() {
    return {
      account: '',
      password: '',
      email: ''
    };
  },
  methods: {
    register: function register() {
      /**
       * 客户端对账号信息进行一些必要的校验。
       * 实际开发中，根据业务需要进行处理，这里仅做示例。
       */
      if (this.account.length < 5) {
        uni.showToast({
          icon: 'none',
          title: '账号最短为 5 个字符'
        });
        return;
      }

      if (this.password.length < 6) {
        uni.showToast({
          icon: 'none',
          title: '密码最短为 6 个字符'
        });
        return;
      }

      if (this.email.length < 3 || !~this.email.indexOf('@')) {
        uni.showToast({
          icon: 'none',
          title: '邮箱地址不合法'
        });
        return;
      }

      var data = {
        account: this.account,
        password: this.password,
        email: this.email
      };
      service.addUser(data);
      uni.showToast({
        title: '注册成功'
      });
      uni.navigateBack({
        delta: 1
      });
    }
  }
};
